$CFP = $this->config->get('callforprice');

if (isset($CFP) && ($CFP['Enabled'] == 'yes')) {
	if ($CFP['Alert']=='default') {
		$json['CFP'] = true;
	} else if ($CFP['Alert']=='selected') {
		if (isset($CFP['Products']) && (in_array($this->request->post['product_id'],$CFP['Products'])) )
			$json['CFP'] = true;
	}
}

